// LoginPage.jsx
import React, { useState } from 'react';
import '../styles/LoginPage.css'
import Particles from '@tsparticles/react';

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSignIn = (e) => {
    e.preventDefault();
    // Here you would handle the sign-in logic, possibly using a service function
    console.log('Sign In:', username, password);
  };

  return (
    <div className="login-page">
   <Particles
    id="tsparticles"
    options={{
        fullScreen: {
            enable: true,
            zIndex: -1 // Ensure particles are in the background
        },
        particles: {
            number: {
                value: 80, // Number of particles
                density: {
                    enable: true,
                    value_area: 800 // Density of particles
                }
            },
            color: {
                value: "#ffffff" // Color of the particles
            },
            shape: {
                type: "circle", // Shape of the particles
            },
            opacity: {
                value: 0.5,
                random: true,
                anim: {
                    enable: true,
                    speed: 1,
                    opacity_min: 0.1,
                    sync: false
                }
            },
            size: {
                value: 10,
                random: true,
                anim: {
                    enable: true,
                    speed: 10,
                    size_min: 0.1,
                    sync: false
                }
            },
            line_linked: {
                enable: false, // Disable lines between particles
            },
            move: {
                enable: true,
                speed: 2,
                direction: "top", // Particles move upwards
                random: false,
                straight: false,
                out_mode: "out", // Particles exit the canvas boundaries
                bounce: false,
                attract: {
                    enable: false,
                    rotateX: 600,
                    rotateY: 1200
                }
            }
        },
        interactivity: {
            detect_on: "canvas",
            events: {
                onhover: {
                    enable: true,
                    mode: "bubble" // Particles grow on mouse hover
                },
                onclick: {
                    enable: true,
                    mode: "push" // Add particles on click
                },
                resize: true
            },
            modes: {
                bubble: {
                    distance: 400,
                    size: 20,
                    duration: 2,
                    opacity: 0.8,
                    speed: 3
                },
                push: {
                    particles_nb: 4
                }
            }
        },
        retina_detect: true
    }}
/>
    <div className="login-container">
      <form className="login-form" onSubmit={handleSignIn}>
        <div className='username'>
          <input
            type="text"
            placeholder="UserName"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          </div>
          <div className='password'>
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div className='button'>
          <button type="submit">Sign In</button>
          <span>or</span>
          <button type="button">Sign Up</button>
        </div>

      </form>
    </div>
    </div>
  );
};

export default LoginPage;
